import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Button, Alert, Image } from 'react-native';
import axios from 'axios';
import plus from '../assets/plus.png';
export default class Main extends React.Component {
  state = {
    email: "",
    password: ""
  }
  onPress = (() => {

  });

  render() {
    return (
      <View style={styles.container}>
        <Text style={styles.logo}>Records</Text>

        <View style={styles.inputView}>
          <TouchableOpacity activeOpacity={0.5}>
            <Text>+ Add records</Text>
          </TouchableOpacity>
        </View>
        <View style={styles.inputView} >
          <TextInput
            secureTextEntry
            style={styles.inputText}
            placeholder="Password..."
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ password: text })} />
        </View>
        <TouchableOpacity style={styles.loginBtn} onPress={this.onPress}>
          <Text
            style={styles.loginText}
          >LOGIN</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.signupBtn}>
          <Text style={styles.loginText}>Signup</Text>
        </TouchableOpacity>


      </View >
    );
  }
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#0F8CCF',
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    fontWeight: "bold",
    fontSize: 50,
    marginBottom: 40
  },
  inputView: {
    width: "80%",
    borderRadius: 25,
    height: 50,
    marginBottom: 20,
    justifyContent: "center",
    padding: 20,
    backgroundColor: 'white',
  },
  inputText: {
    height: 50,
    color: "white"
  },
  forgot: {
    color: "white",
    fontSize: 11
  },
  loginBtn: {
    width: "80%",
    backgroundColor: "#72EA6F",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    marginBottom: 10
  },
  signupBtn: {
    width: "80%",
    backgroundColor: "#599BDA",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 10
  },
  loginText: {
    color: "white"
  },
  addBtn: {
    width: "80%",
    backgroundColor: "#72EA6F",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    marginBottom: 10
  },
});
