import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Button, Alert } from 'react-native';
import axios from 'axios';


const userRequest = axios.create({
  baseURL: 'http://119.237.83.221:3000/users/'
});

export default class Register extends React.Component {
  state = {
    email: "",
    password: "",
    phone: "",
    clinicName: ""
  }
  onSignUp = (() => {
    userRequest.post('/registerUser', {
      user: {
        email: this.state.email,
        password: this.state.password,
        phone: this.state.phone,
        clinicName: this.state.clinicName
      }
    }).then(res => {
      alert(res.data.msg);
      if (res.data.status == 'success') {
        this.props.navigation.navigate('Login')
      }
    });
  });

  render() {
    return (
      <View style={styles.container}>
        <Text style={styles.logo}>Register</Text>
        <View style={styles.inputView} >
          <TextInput
            style={styles.inputText}
            placeholder="Email..."
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ email: text })} />
        </View>
        <View style={styles.inputView} >
          <TextInput
            secureTextEntry
            style={styles.inputText}
            placeholder="Password..."
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ password: text })} />
        </View>
        <View style={styles.inputView} >
          <TextInput
            style={styles.inputText}
            placeholder="Clinic Name..."
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ clinicName: text })} />
        </View>
        <View style={styles.inputView} >
          <TextInput
            style={styles.inputText}
            placeholder="Phone..."
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ phone: text })} />
        </View>

        <TouchableOpacity style={styles.loginBtn} onPress={this.onSignUp}>
          <Text
            style={styles.loginText}
          >SignUp</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.signupBtn} onPress={() => {
          this.props.navigation.navigate('Login')
        }}>
          <Text style={styles.loginText}>Back</Text>
        </TouchableOpacity>


      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#0F8CCF',
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    fontWeight: "bold",
    fontSize: 50,
    marginBottom: 40
  },
  inputView: {
    width: "80%",
    borderRadius: 25,
    height: 50,
    marginBottom: 20,
    justifyContent: "center",
    padding: 20,
    backgroundColor: 'white',
  },
  inputText: {
    height: 50,
    color: "black"
  },
  forgot: {
    color: "white",
    fontSize: 11
  },
  loginBtn: {
    width: "80%",
    backgroundColor: "#72EA6F",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    marginBottom: 10
  },
  signupBtn: {
    width: "80%",
    backgroundColor: "#599BDA",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 10
  },
  loginText: {
    color: "white"
  }
});
